<#    La structure if else permet d'exécuter du code en fonction d'une condition    Syntaxe    if(Condition(s)){                Bloc de script    }    else{  => Facultatif        Bloc de script qui sera exécuté si on est pas passé dans le bloc if    }#># On déclare une note$Note = Get-Random -Minimum 0 -Maximum 20# Si la note est supérieure ou égale à 10if($Note -ge 10){    Write-Host "L'élève est admis !" -ForegroundColor Green}else{    Write-Host "L'élève est recalé !" -ForegroundColor Gray}